SELECT TRANS_CODE FROM TB_USER_TRANS_COM WHERE USER_ID = #{userId} AND ORG_NATION = 
	ISNULL((SELECT NATION_CODE FROM TB_STATION WHERE STATION_NAME = #{orgStationName}),(SELECT NATION_CODE FROM TB_STATION WHERE STATION_CODE = #{orgStation})) 
	AND DSTN_NATION = #{dstnNation};

SELECT COUNT(*) FROM ( SELECT ROW_NUMBER() OVER(ORDER BY F1.NNO DESC) Row_Num, (SELECT STATION_NAME FROM TB_STATION WHERE STATION_CODE = ORG_STATION) Station_Name , CASE WHEN DATEDIFF(HOUR,W_DATE,GETDATE()) >= 24 THEN 'Y' ELSE ISNULL((SELECT APPRV FROM TB_USER_RETURN_APPRV WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION),'N') END APPRV_YN, CNEE_CNTRY AS Receiver_Nation, CNEE_NAME AS Receiver_Name, CNEE_STATE AS Receiver_State, CNEE_CITY AS Receiver_City, CNEE_DISTRICT AS Receiver_District, CNEE_ZIP AS Receiver_Zip, CNEE_ADDR AS Receiver_Address, CNEE_ADDR_DETAIL AS Receiver_Address_Detail, CNEE_TEL AS Receiver_Tel, CNEE_HP AS Receiver_Hp, CNEE_EMAIL AS Receiver_Email , TRK_COM AS Request_Delivery_Company, TRK_NO AS Request_BL, TRK_NO_BL AS Request_BL_ImgUrl, W_DATE AS Registration_Date , CASE WHEN (SELECT COUNT(*) FROM TB_RETURN_LIST WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) = 0 THEN 'N' ELSE 'Y' END AS Acceptance_Status, (SELECT W_DATE FROM TB_RETURN_LIST WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) Acceptance_Date, (SELECT ETC_VALUE FROM TB_ETC WHERE ETC_TYPE = 'RETURN' AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) Cost_Value , (SELECT ETC_CURRENCY FROM TB_ETC WHERE ETC_TYPE = 'RETURN' AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) Cost_Currency FROM TB_REQ_RETURN_LIST F1 WHERE RETURN_NO LIKE CONCAT('%',#{returnNo},'%')
AND CONVERT(VARCHAR(10),W_DATE,112) BETWEEN #{fromDate} AND #{toDate}
AND ORG_STATION IN (SELECT STATION_CODE FROM TB_STATION WHERE (STATION_CODE LIKE CONCAT('%',#{station},'%') or STATION_NAME LIKE CONCAT('%',#{station},'%'))) AND USER_ID = #{userId}) M1

SELECT Departure_Station,Return_No,Apprv_Yn, Receiver_Nation,Receiver_Name,Receiver_State,Receiver_City, Receiver_District,Receiver_Zip,Receiver_Address,Receiver_Address_Detail, Receiver_Tel,Receiver_Hp,Receiver_Email,Request_Delivery_Company, Request_BL,Request_BL_ImgUrl,Registration_Date, Acceptance_Status,Acceptance_Date,Cost_Value,Cost_Currency FROM ( SELECT ROW_NUMBER() OVER(ORDER BY F1.NNO DESC) Row_Num, ISNULL((SELECT STATION_NAME FROM TB_STATION WHERE STATION_CODE = ORG_STATION),'') AS Departure_Station , ISNULL(RETURN_NO,'') AS Return_No , ISNULL(CASE WHEN DATEDIFF(HOUR,W_DATE,GETDATE()) >= 24 THEN 'Y' ELSE ISNULL((SELECT APPRV FROM TB_USER_RETURN_APPRV WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION),'N') END,'') Apprv_Yn, ISNULL(CNEE_CNTRY,'') AS Receiver_Nation, ISNULL(CNEE_NAME,'') AS Receiver_Name, ISNULL(CNEE_STATE,'') AS Receiver_State, ISNULL(CNEE_CITY,'') AS Receiver_City, ISNULL(CNEE_DISTRICT,'') AS Receiver_District, ISNULL(CNEE_ZIP,'') AS Receiver_Zip, ISNULL(CNEE_ADDR,'') AS Receiver_Address, ISNULL(CNEE_ADDR_DETAIL,'') AS Receiver_Address_Detail, ISNULL(CNEE_TEL,'') AS Receiver_Tel, ISNULL(CNEE_HP,'') AS Receiver_Hp, ISNULL(CNEE_EMAIL,'') AS Receiver_Email , ISNULL(TRK_COM,'') AS Request_Delivery_Company, ISNULL(TRK_NO,'') AS Request_BL, ISNULL(TRK_NO_BL,'') AS Request_BL_ImgUrl, ISNULL(CONVERT(VARCHAR(23),W_DATE,20),'') AS Registration_Date , ISNULL(CASE WHEN (SELECT COUNT(*) FROM TB_RETURN_LIST WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) = 0 THEN 'N' ELSE 'Y' END,'') AS Acceptance_Status, ISNULL((SELECT W_DATE FROM TB_RETURN_LIST WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION),'') Acceptance_Date, ISNULL((SELECT CONVERT(VARCHAR,ETC_VALUE) FROM TB_ETC WHERE ETC_TYPE = 'RETURN' AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION),'') Cost_Value , ISNULL((SELECT ETC_CURRENCY FROM TB_ETC WHERE ETC_TYPE = 'RETURN' AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION),'') Cost_Currency FROM TB_REQ_RETURN_LIST F1 WHERE RETURN_NO LIKE CONCAT('%',#{returnNo},'%')
AND CONVERT(VARCHAR(10),W_DATE,112) BETWEEN #{fromDate} AND #{toDate}
AND ORG_STATION IN (SELECT STATION_CODE FROM TB_STATION WHERE (STATION_CODE LIKE CONCAT('%',#{station},'%') or STATION_NAME LIKE CONCAT('%',#{station},'%'))) AND USER_ID = #{userId} ) M1 WHERE M1.Row_Num BETWEEN #{paging.boardStart} and #{paging.boardEnd}

SELECT COUNT(*) FROM ( SELECT (SELECT STATION_NAME FROM TB_STATION WHERE STATION_CODE = F1.ORG_STATION) as station , F1.ORG_STATION AS orgStation , F1.USER_ID as userId, F2.ORDER_DATE as orderDate, F2.ORDER_NO as orderNo, F2.SHIPPER_NAME as shipperName, F1.NNO as nno FROM TB_STOCK F1 , TB_ORDER_LIST F2 WHERE F2.SHIPPER_NAME LIKE CONCAT('%','','%') AND F1.SUB_NO IN ( SELECT SUB_NO FROM TB_ORDER_ITEM WHERE ITEM_DETAIL LIKE CONCAT('%','','%') AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION ) AND F2.ORDER_NO LIKE CONCAT('%',#{orderNo},'%')
AND CASE WHEN ISNULL(F1.WH_IN_DATE,'') = '' THEN CONVERT(NVARCHAR(8),F1.W_DATE,112) ELSE WH_IN_DATE END BETWEEN #{fromDate} AND #{toDate}
AND CASE WHEN ISNULL(F1.OUT_NNO,'') = '' THEN 'N' ELSE 'Y' END LIKE CONCAT('%',#{outYn},'%') AND F1.NNO = F2.NNO AND F1.USER_ID = F2.USER_ID AND F1.ORG_STATION = F2.ORG_STATION AND F1.USER_ID LIKE CONCAT('%',#{userId},'%') AND F1.ORG_STATION IN (SELECT STATION_CODE FROM TB_STATION WHERE STATION_NAME LIKE CONCAT('%',#{stationName},'%')) GROUP BY F1.ORG_STATION , F1.USER_ID , F1.NNO, F1.SUB_NO, F2.ORG_STATION, F2.USER_ID , F2.ORDER_NO, F2.ORDER_DATE, F2.SHIPPER_NAME ) M1

