SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY F1. NNO ) NUM, ROW_NUMBER() OVER(ORDER BY F1.NNO DESC) ROWNUM, (SELECT STATION_NAME FROM TB_STATION WHERE STATION_CODE = F1.ORG_STATION) as station , F1.ORG_STATION AS orgStation , F1.USER_ID as userId, F2.ORDER_DATE as orderDate, F2.ORDER_NO as orderNo, F2.SHIPPER_NAME as shipperName, F1.NNO AS nno , MIN(CASE WHEN ISNULL(F1.WH_IN_DATE,'') = '' THEN CONVERT(NVARCHAR(8),F1.W_DATE,112) ELSE WH_IN_DATE END) AS minWhInDate , MIN(CASE WHEN ISNULL(F1.WH_IN_DATE,'') = '' THEN CONVERT(NVARCHAR(8),F1.W_DATE,112) ELSE WH_IN_DATE END) AS maxWhInDate , (SELECT COUNT(*) FROM TB_STOCK WHERE WH_STATUS = 'WO' AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) AS Total_Wo_Status_Cnt, (SELECT COUNT(*) FROM TB_STOCK WHERE WH_STATUS = 'WF' AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) AS Total_Wf_Status_Cnt, dbo.FN_STOCK_OUT_DATE(F1.NNO,F1.USER_ID,F1.ORG_STATION) AS Shipping_Date, ISNULL(F2.HAWB_NO,'') AS BL_No , ISNULL(CONVERT(NVARCHAR(100),(SELECT APPLY_WTA FROM VI_APPLY_WT_INFO WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION)),'') AS Actual_Weight, ISNULL(CONVERT(NVARCHAR(100),(SELECT APPLY_WTC FROM VI_APPLY_WT_INFO WHERE NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION)),'') AS Volume_Weight, ISNULL(case when convert(DECIMAL(38,2),dbo.FN_DELIVERY_CHG(F1.ORG_STATION,F1.USER_ID,F1.NNO,'')) = 999999999.00 then '' else convert(nvarchar(100),convert(DECIMAL(38,2),dbo.FN_DELIVERY_CHG(F1.ORG_STATION,F1.USER_ID,F1.NNO,''))) end,'') AS Shipping_Fee FROM TB_STOCK F1 , TB_ORDER_LIST F2 WHERE F2.SHIPPER_NAME LIKE CONCAT('%','','%') AND F1.SUB_NO IN (SELECT SUB_NO FROM TB_ORDER_ITEM WHERE ITEM_DETAIL LIKE CONCAT('%','','%') AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) AND F2.ORDER_NO LIKE CONCAT('%',#{orderNo},'%')
AND CASE WHEN ISNULL(F1.WH_IN_DATE,'') = '' THEN CONVERT(NVARCHAR(8),F1.W_DATE,112) ELSE WH_IN_DATE END BETWEEN #{fromDate} AND #{toDate}
AND F1.WH_STATUS LIKE CONCAT('%',#{whStatus},'%') AND CASE WHEN ISNULL(F1.OUT_NNO,'') = '' THEN 'N' ELSE 'Y' END LIKE CONCAT('%',#{outYn},'%') AND F1.NNO = F2.NNO AND F1.USER_ID = F2.USER_ID AND F1.ORG_STATION = F2.ORG_STATION AND F1.USER_ID LIKE CONCAT('%',#{userId},'%') AND F1.ORG_STATION IN (SELECT STATION_CODE FROM TB_STATION WHERE STATION_NAME LIKE CONCAT('%',#{stationName},'%')) GROUP BY F1.ORG_STATION , F1.USER_ID , F1.NNO, F2.ORG_STATION, F2.USER_ID , F2.ORDER_NO, F2.HAWB_NO, F2.ORDER_DATE, F2.SHIPPER_NAME ) M1 WHERE M1.ROWNUM BETWEEN #{paging.boardStart} and #{paging.boardEnd}

