AND F1.WH_STATUS LIKE CONCAT('%',#{whStatus},'%') AND CASE WHEN ISNULL(F1.OUT_NNO,'') = '' THEN 'N' ELSE 'Y' END LIKE CONCAT('%',#{outYn},'%') AND F1.NNO = F2.NNO AND F1.USER_ID = F2.USER_ID AND F1.ORG_STATION = F2.ORG_STATION AND F1.USER_ID LIKE CONCAT('%',#{userId},'%') AND F1.ORG_STATION IN (SELECT STATION_CODE FROM TB_STATION WHERE STATION_NAME LIKE CONCAT('%',#{stationName},'%')) GROUP BY F1.ORG_STATION , F1.USER_ID , F1.NNO, F2.ORG_STATION, F2.USER_ID , F2.ORDER_NO, F2.ORDER_DATE, F2.SHIPPER_NAME ) M1

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY F1. NNO ) NUM, ROW_NUMBER() OVER(ORDER BY F1.NNO DESC) ROWNUM, (SELECT STATION_NAME FROM TB_STATION WHERE STATION_CODE = F1.ORG_STATION) as station , F1.ORG_STATION AS orgStation , F1.USER_ID as userId, F2.ORDER_DATE as orderDate, F2.ORDER_NO as orderNo, F2.SHIPPER_NAME as shipperName, F1.NNO as nno , MIN(CASE WHEN ISNULL(F1.WH_IN_DATE,'') = '' THEN CONVERT(NVARCHAR(8),F1.W_DATE,112) ELSE WH_IN_DATE END) AS minWhInDate , MIN(CASE WHEN ISNULL(F1.WH_IN_DATE,'') = '' THEN CONVERT(NVARCHAR(8),F1.W_DATE,112) ELSE WH_IN_DATE END) AS maxWhInDate , (SELECT COUNT(*) FROM TB_STOCK WHERE NNO = F1.NNO AND WH_STATUS = 'WO' AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) Total_Wo_Status_Cnt, (SELECT COUNT(*) FROM TB_STOCK WHERE NNO = F1.NNO AND WH_STATUS = 'Wf' AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) Total_Wf_Status_Cnt, ISNULL(dbo.FN_STOCK_OUT_INFO(F1.NNO,F1.USER_ID,F1.ORG_STATION,'OUT_DATE'),'') AS OUT_DATE, ISNULL(dbo.FN_STOCK_OUT_INFO(F1.NNO,F1.USER_ID,F1.ORG_STATION,'HAWB_NO'),'') AS OUT_BL FROM TB_STOCK F1 , TB_ORDER_LIST F2 WHERE F2.SHIPPER_NAME LIKE CONCAT('%','','%') AND F1.SUB_NO IN (SELECT SUB_NO FROM TB_ORDER_ITEM WHERE ITEM_DETAIL LIKE CONCAT('%','','%') AND NNO = F1.NNO AND USER_ID = F1.USER_ID AND ORG_STATION = F1.ORG_STATION) AND F2.ORDER_NO LIKE CONCAT('%',#{orderNo},'%')
