AND F1.WH_STATUS LIKE CONCAT('%',#{whStatus},'%') AND CASE WHEN ISNULL(F1.OUT_NNO,'') = '' THEN 'N' ELSE 'Y' END LIKE CONCAT('%',#{outYn},'%') AND F1.NNO = F2.NNO AND F1.USER_ID = F2.USER_ID AND F1.ORG_STATION = F2.ORG_STATION AND F1.USER_ID LIKE CONCAT('%',#{userId},'%') AND F1.ORG_STATION IN (SELECT STATION_CODE FROM TB_STATION WHERE STATION_NAME LIKE CONCAT('%',#{stationName},'%')) GROUP BY F1.ORG_STATION , F1.USER_ID , F1.NNO, F2.ORG_STATION, F2.USER_ID , F2.ORDER_NO, F2.ORDER_DATE, F2.SHIPPER_NAME ) M1 WHERE M1.ROWNUM BETWEEN #{paging.boardStart} and #{paging.boardEnd}

          SELECT STOCK_TYEP Stock_Type ,
                 WH_STATUS WhIn_Status,
                 (SELECT NAME FROM TB_CODE WHERE CODE_DIV = 'WH_STATUS' AND CODE = WH_STATUS) Wh_Status_Name,
                 STOCK_NO  as Stock_No ,
                 ISNULL(dbo.FN_STOCK_OUT_INFO(F1.NNO,F1.USER_ID,F1.ORG_STATION,'OUT_DATE'),'') AS Shipping_Date,
				 ISNULL(dbo.FN_STOCK_OUT_INFO(F1.NNO,F1.USER_ID,F1.ORG_STATION,'HAWB_NO'),'') AS BL_No
            FROM TB_STOCK F1 
           WHERE CASE WHEN ISNULL(OUT_NNO,'') = '' THEN 'N' ELSE 'Y' END LIKE CONCAT('%',#{outYn},'%')
            AND STOCK_TYEP = 'INSP_IN'
            AND GROUP_IDX = #{groupIdx} 
            AND NNO = #{nno}
            AND SUB_NO = #{subNo}
            AND USER_ID =  #{userId}
            AND ORG_STATION = #{orgStation}

          SELECT STOCK_TYEP Stock_Type ,
                 WH_STATUS WhIn_Status,
                 (SELECT NAME FROM TB_CODE WHERE CODE_DIV = 'WH_STATUS' AND CODE = WH_STATUS) Wh_Status_Name,
                 STOCK_NO  as Stock_No ,
                 ISNULL(dbo.FN_STOCK_OUT_INFO(F1.NNO,F1.USER_ID,F1.ORG_STATION,'OUT_DATE'),'') AS Shipping_Date,
				 ISNULL(dbo.FN_STOCK_OUT_INFO(F1.NNO,F1.USER_ID,F1.ORG_STATION,'HAWB_NO'),'') AS BL_No
            FROM TB_STOCK F1 
           WHERE CASE WHEN ISNULL(OUT_NNO,'') = '' THEN 'N' ELSE 'Y' END LIKE CONCAT('%',#{outYn},'%')
            AND STOCK_TYEP = 'INSP_ADD_IN'
            AND GROUP_IDX = #{groupIdx} 
            AND NNO = #{nno}
            AND SUB_NO = #{subNo}
            AND USER_ID =  #{userId}
            AND ORG_STATION = #{orgStation}

         SELECT CASE WHEN ADMIN_YN = 'Y' THEN '' ELSE '' END AS User_Div,
			       WH_MEMO AS Msg , W_DATE AS Registration_Date
			 FROM TB_STOCK_MSG F1
			WHERE ISNULL(ORDER_NO,'') != '' 
			  and MSG_DIV = 'MSG'
			  AND ORDER_NO = #{orderNo}
			  AND USER_ID =  #{userId}
			  AND ORG_STATION = #{orgStation}

